sidebarCSS:
#root.root {--tabs-close-btn-margin: 3px;}
#root.root {--general-margin: 0px;}
#root {
  --tabs-font: 9pt Segoe UI;
  --tabs-count-font: .625rem Segoe UI;
  --bookmarks-bookmark-font: .875rem Segoe UI;
  --bookmarks-folder-font: 9pt Segoe UI;
  --nav-btn-width: 42px;
  --nav-btn-height: 42px;
}

/* Adjust styles according to sidebar width */
@media screen and (max-width: 48px) {
  #root {
    --tabs-indent: unset;
  }

  .Tab .close {
    visibility: hidden;
  }

  .BottomBar {
    visibility: collapse;
  }
}

#root {
  --tabs-height: 36px;
}

#root:hover .Tab .close {
  transition: visibility 0s 100ms;
}

.central-box {
	padding-top: 5px;
}

.ScrollBox > .scroll-container {
  width: calc(100% + 4px);
  overflow-y: scroll;
}

.TabsPanel .new-tab-btns .new-tab-btn {
  left: 2px;
}

.Tab[data-active="true"] .body {
  background: transparent;
  box-shadow: none;
}

/* Background layer */
.Tab .body:before {
  content: '';
  position: absolute;
  left: 9px;
  top: 3px;
  width: calc(100% - 14px);
  height: calc(100% - 6px);
  border-radius: 4px;
  z-index: -1;
}

.PinnedTabsBar .Tab .body:before {
  width: calc(100% - 18px);
}

.Tab[data-active="true"] .body:before,
.Tab[data-active="true"]:hover .body:before {
  background-color: var(--frame-el-overlay-selected-bg);
  box-shadow: 0px 3px 5px rgba(0, 0, 0, 0.5) !important;
}

.Tab[data-active="true"][data-group="true"] .ctx,
.Tab[data-active="true"][data-group="true"][data-colorized="true"] .color-layer {
  box-shadow: 0px 3px 5px rgba(0, 0, 0, 0.5) !important;
}

/* Resize and reposition favicons */
.Tab .fav {
  margin-left: 16px !important;
}

.Tab .audio {
  width: 16px !important;
  height: 16px !important;
  z-index: 21 !important;
  left: calc(var(--tabs-inner-gap) + 24px) !important;
}

.Tab .close {
  margin: 0px 2px;
}

.PinnedTabsBar .Tab .close {
  margin: 0px 6px;
}

.Tab .t-box {
  width: calc(100% - 50px);
  mask: none !important;
}

.Tab:hover .t-box {
  width: calc(100% - 70px);
}

.Tab .t-box .title {
  text-overflow: ellipsis;
}

.Tab[data-audible="true"] .t-box .title,
.Tab[data-muted="true"] .t-box .title,
.Tab[data-paused="true"] .t-box, .title {
  transform: translateX(calc(var(--audio-btn-offset) / 2));
}

.Tab .ctx,
.Tab[data-colorized="true"] .color-layer {
  height: calc(100% + 3px);
  width: 3px;
  left: 2px;
  top: -6px;
  opacity: 1 !important;
  box-shadow: none;
  border-radius: 0 0 3px 3px;
}

.Tab[data-group="true"] + .Tab[data-group="false"][data-color] .ctx,
.Tab[data-group="true"][data-colorized="true"] + .Tab[data-parent="false"][data-colorized="true"] .color-layer {
  top: 0;
  height: calc(100% - 3px);
  border-radius: 3px;
}

.Tab[data-parent="true"][data-group="false"][data-colorized="true"] .color-layer {
  top: 2px;
  height: calc(100% - 5px);
  border-radius: 3px;
}

.Tab[data-color],
.Tab[data-colorized="true"] {
  padding-left: 0 !important;
}

.Tab[data-group="true"][data-colorized="true"] .body:before {
  all: unset !important;
}

.Tab[data-group="true"][data-colorized="true"] .color-layer {
  height: 24px;
  width: calc(100% - 20px);
  left: 12px;
  top: 6px;
  z-index: 0;
  opacity: 1;
  border-radius: 3px;
}

.Tab[data-group="true"][data-colorized="true"] .title {
  color: black;
}

.Tab[data-group="true"][data-colorized="true"] .fav svg,
.Tab[data-group="true"][data-colorized="true"] .close svg {
  fill: black;
}

.Tab[data-group="true"][data-colorized="true"] .close {
  --tabs-close-btn-margin: 6px;
}

.Tab[data-group="true"][data-colorized="true"] .t-box,
.Tab[data-group="true"][data-colorized="true"] .close {
  opacity: 1;
}

.Tab[data-group="true"][data-colorized="true"] .body .fav,
.Tab[data-group="true"][data-colorized="true"] .fav .exp,
.Tab[data-group="true"][data-colorized="true"] .fav .exp svg {
  opacity: 1;
}

.Tab[data-group="true"][data-colorized="true"] .fav .fav-icon,
.Tab[data-group="true"][data-colorized="true"] .fav .child-count {
  display: none;
}

.Tab:hover .fav,
.PinnedTab:hover .fav {
  opacity: 1;
}
